const https = require('https');

const paytm_config = require('./paytm/paytm_config').paytm_config;
const router = require('express').Router()

router.post('/', (request, res) => {
    /* initialize an object */
    var paytmParams = {};
    const orderid = request.body.orderid
    /* body parameters */
    paytmParams.body = {

        /* enter six digit bin here */
        "bin" : request.body.bin,
    };

    /* head parameters */
    paytmParams.head = {

        /* put transaction token here, this is same token which was generated by Initiate Transaction API */
        "txnToken" : request.body.token
    };

    /* prepare JSON string for request */
    var post_data = JSON.stringify(paytmParams);

    var options = {

        /* for Staging */
        hostname: 'securegw-stage.paytm.in',

        /* for Production */
        // hostname: 'securegw.paytm.in',

        port: 443,
        path: `/fetchBinDetail?mid=${paytm_config.MID}&orderId=${orderid}`,
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Content-Length': post_data.length
        }
    };

    // Set up the request
    var response = "";
    var post_req = https.request(options, function(post_res) {
        post_res.on('data', function (chunk) {
            response += chunk;
        });

        post_res.on('end', function(){
            console.log('Response: ', response);
        });
    });

    // post the data
    post_req.write(post_data);
    post_req.end();

});

module.exports = router;